    "import pandas as pd\n",
    "import matplotlib.pyplot as plt\n",
    "import numpy as np\n",
    "\n",
    "# Load the CSV data\n",
    "file_path = r'C:\\Users\\judit\\OneDrive - Imperial College London\\MSE Yr2\\UROPs\\Materials\\Judy AFM\\Dataset4\\Data5.xyz'\n",
    "data = pd.read_csv(file_path, delim_whitespace=True, header=None, names=['X', 'Y', 'Z'])\n",
    "\n",
    "# Group the data by y-axis values and calculate the average z-value for each y to create a profile plot\n",
    "profile_data_y = data.groupby('Y')['Z'].mean().reset_index()\n",
    "\n",
    "# Detect peaks in the profile data\n",
    "peak_threshold = 0.0000000125  # This threshold might need adjustment\n",
    "profile_data_y['Z_diff'] = profile_data_y['Z'].diff().abs()\n",
    "\n",
    "# Identify the peaks by finding points where the difference exceeds the threshold\n",
    "peak_indices = profile_data_y[profile_data_y['Z_diff'] > peak_threshold].index\n",
    "\n",
    "if not peak_indices.empty:\n",
    "    peak_range_y_min = profile_data_y.loc[peak_indices.min(), 'Y']\n",
    "    peak_range_y_max = profile_data_y.loc[peak_indices.max(), 'Y']\n",
    "\n",
    "    # Irradiated and unirradiated regions\n",
    "    irradiated_region = profile_data_y[profile_data_y['Y'] < peak_range_y_min]\n",
    "    unirradiated_region = profile_data_y[profile_data_y['Y'] > peak_range_y_max]\n",
    "\n",
    "    # Calculate a single slope (a) for both lines by averaging the slopes from the two regions\n",
    "    slope_irradiated = np.polyfit(irradiated_region['Y'], irradiated_region['Z'], 1)[0]\n",
    "    slope_unirradiated = np.polyfit(unirradiated_region['Y'], unirradiated_region['Z'], 1)[0]\n",
    "    average_slope = (slope_irradiated + slope_unirradiated) / 2\n",
    "\n",
    "    # Calculate the intercept (b) for each region using the common slope\n",
    "    intercept_irradiated = irradiated_region['Z'].mean() - average_slope * irradiated_region['Y'].mean()\n",
    "    intercept_unirradiated = unirradiated_region['Z'].mean() - average_slope * unirradiated_region['Y'].mean()\n",
    "\n",
    "    # Create the trend lines using the common slope and different intercepts\n",
    "    y_extended = np.linspace(profile_data_y['Y'].min(), profile_data_y['Y'].max(), 500)  # Create a smooth y-axis range\n",
    "    trend_irradiated_parallel = average_slope * y_extended + intercept_irradiated\n",
    "    trend_unirradiated_parallel = average_slope * y_extended + intercept_unirradiated\n",
    "\n",
    "    # Calculate the difference in intercepts (b values)\n",
    "    intercept_diff = intercept_unirradiated - intercept_irradiated\n",
    "\n",
    "    # Create a 2D plot for the y-axis vs. height (z-axis)\n",
    "    plt.figure(figsize=(10, 6))\n",
    "    plt.plot(profile_data_y['Y'], profile_data_y['Z'], label='Average Height Profile', color='blue')\n",
    "\n",
    "    # Highlight the peak region with a shaded area\n",
    "    plt.axvspan(peak_range_y_min, peak_range_y_max, color='blue', alpha=0.3, label='Peak Region')\n",
    "\n",
    "    # Plot the parallel lines (same slope, different intercepts)\n",
    "    plt.plot(y_extended, trend_irradiated_parallel, color='red', linestyle='--', label='Irradiated Trend (Parallel)')\n",
    "    plt.plot(y_extended, trend_unirradiated_parallel, color='green', linestyle='--', label='Unirradiated Trend (Parallel)')\n",
    "\n",
    "    # Adding labels and title\n",
    "    plt.xlabel('Position (y-axis) / m')\n",
    "    plt.ylabel('Height / m')\n",
    "    plt.legend()\n",
    "\n",
    "    plt.grid(False)\n",
    "    plt.savefig('Height profile0.png')\n",
    "    plt.show()\n",
    "\n",
    "    # Print the results\n",
    "    print(f\"Common slope (a) for both regions: {average_slope:.6f}\")\n",
    "    print(f\"Intercept for irradiated region (b_irradiated): {intercept_irradiated:.6f}\")\n",
    "    print(f\"Intercept for unirradiated region (b_unirradiated): {intercept_unirradiated:.6f}\")\n",
    "    print(f\"Height difference between the two lines (difference in intercepts): {intercept_diff*1e6} um\")\n",
    "\n",
    "else:\n",
    "    print(\"No significant peaks detected with the given threshold.\")\n"
